import React, { useState, useEffect } from 'react';
import './SimulationPage.css';
import bowlingImage from '../images/bowling.jpg';

function SimulationPage() {
  const [frames, setFrames] = useState([]);
  const [totalScore, setTotalScore] = useState(0);
  const [gameOver, setGameOver] = useState(false);

  // On mount, fetch current game state (in case there's an existing in-progress game)
  useEffect(() => {
    fetchGameState();
  }, []);

  const fetchGameState = async () => {
    const res = await fetch('http://localhost:3001/api/gameState');
    const data = await res.json();
    setFrames(data.frames);
    setTotalScore(data.totalScore);
    setGameOver(data.gameOver);
  };

  const handleSimulate = async () => {
    const res = await fetch('http://localhost:3001/api/simulateRoll', {
      method: 'POST',
    });
    const data = await res.json();
    setFrames(data.frames);
    setTotalScore(data.totalScore);
    setGameOver(data.gameOver);
  };

  const handleSimulateGame = async () => {
    console.log('Simulating game...');
    const rest = await fetch('http://localhost:3001/api/simulateGame', {
      method: 'POST',
    });
    const data = await rest.json();
    setFrames(data.frames);
    setTotalScore(data.totalScore);
    setGameOver(data.gameOver);
  };

  const handleReset = async () => {
    const res = await fetch('http://localhost:3001/api/resetGame', {
      method: 'POST',
    });
    const data = await res.json();
    setFrames(data.frames);
    setTotalScore(data.totalScore);
    setGameOver(data.gameOver);
  };

  const handleUndo = async () => {
    const res = await fetch('http://localhost:3001/api/undoRoll', {
      method: 'POST',
    });
    const data = await res.json();
    setFrames(data.frames);
    setTotalScore(data.totalScore);
    setGameOver(data.gameOver);
  };

  // Format roll display using bowling notation
  const formatRoll = (frame, rollNum) => {
    if (rollNum === 1) {
      // First roll
      if (frame.roll1 === 10) {
        return 'X'; // Strike
      } else if (frame.roll1 !== undefined) {
        return frame.roll1 === 0 ? '-' : frame.roll1; // Show '-' for gutter balls
      }
      return '';
    } else if (rollNum === 2) {
      // Second roll
      if (frame.roll1 === 10 && frame.roll2 === 10) {
        return 'X'; // Strike in 10th frame
      } else if (frame.roll1 !== undefined && frame.roll2 !== undefined && frame.roll1 + frame.roll2 === 10) {
        return '/'; // Spare
      } else if (frame.roll2 !== undefined) {
        return frame.roll2 === 0 ? '-' : frame.roll2; // Show '-' for gutter balls
      }
      return '';
    } else if (rollNum === 3) {
      // Third roll (only in 10th frame)
      if (frame.roll3 === 10) {
        return 'X'; // Strike in bonus roll
      } else if (frame.roll2 === 10 && frame.roll3 !== undefined && frame.roll3 + frame.roll2 === 10) {
        return '/'; // Spare in bonus roll after strike
      } else if (frame.roll3 !== undefined) {
        return frame.roll3 === 0 ? '-' : frame.roll3; // Show '-' for gutter balls
      }
      return '';
    }
    return '';
  };

  console.log('Frames:', frames);
  //console.log('Total Score:', totalScore);
  //console.log('Game Over:', gameOver);

  return (
    <div className="simulation-page">
      <div className="simulation-container">
        <table className="bowling-score-table">
          <thead>
            <tr>
              {/* Frame headers: frames 1â€“9 each have 2 columns, frame 10 has 3 */}
              {[...Array(9)].map((_, i) => (
                <th key={i} colSpan="2">{i + 1}</th>
              ))}
              <th colSpan="3">10</th>
            </tr>
          </thead>
          <tbody>
            {/* First row: display the rolls */}
            <tr>
              {frames.map((frame, i) => {
                // For frames 1â€“9, show roll1 and roll2
                if (i < 9) {
                  return (
                    <React.Fragment key={i}>
                      <td className="frame-shot">
                        {formatRoll(frame, 1)}
                      </td>
                      <td className="frame-shot">
                        {formatRoll(frame, 2)}
                      </td>
                    </React.Fragment>
                  );
                } 
                // For frame 10, show roll1, roll2, roll3
                else {
                  return (
                    <React.Fragment key={i}>
                      <td className="frame-shot">
                        {formatRoll(frame, 1)}
                      </td>
                      <td className="frame-shot">
                        {formatRoll(frame, 2)}
                      </td>
                      <td className="frame-shot">
                        {formatRoll(frame, 3)}
                      </td>
                    </React.Fragment>
                  );
                }
              })}
            </tr>

            {/* Second row: display each frame's cumulative score */}
            <tr>
              {frames.map((frame, i) => {
                if (i < 9) {
                  return (
                    <td 
                      key={`score-${i}`} 
                      colSpan="2" 
                      className="frame-total"
                    >
                      {frame.cumulativeScore || ''}
                    </td>
                  );
                } else {
                  // 10th frame
                  return (
                    <td
                      key={`score-${i}`}
                      colSpan="3"
                      className="frame-total"
                    >
                      {frame.cumulativeScore || ''}
                    </td>
                  );
                }
              })}
            </tr>
          </tbody>
        </table>
      </div>

      <img src={bowlingImage} alt="Bowling" className="bowling-image" />

      <div className="simulation-controls">
        <div className="button-row">
          <button type="button" className="play-button" disabled={gameOver}>
            Play
          </button>
          <button type="button" className="play-button" onClick={handleSimulate} disabled={gameOver}>
            Simulate
          </button>
          <button type="button" className="play-button" onClick={handleSimulateGame} disabled={gameOver}>
            Simulate&nbsp;Game
          </button>
        </div>

        <div className="button-row">
          <button type="button" className="undo-button" onClick={handleUndo}>
            Undo
          </button>
          <button type="button" className="reset-button" onClick={handleReset}>
            Reset
          </button>
        </div>
      </div>
    </div>
  );
}

export default SimulationPage;
