import React, { useState, useEffect } from 'react';
import './SimulationPage.css';
import BowlingLane from '../components/BowlingLane';

function SimulationPage() {
  const [frames, setFrames] = useState([]);
  const [totalScore, setTotalScore] = useState(0);
  const [gameOver, setGameOver] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [currentFrameIndex, setCurrentFrameIndex] = useState(0);
  const [isInteractive, setIsInteractive] = useState(false);
  const [pinsState, setPinsState] = useState(Array(10).fill(true));
  const [selectedPins, setSelectedPins] = useState([]);

  // On mount, fetch current game state (in case there's an existing in-progress game)
  useEffect(() => {
    fetchGameState();
  }, []);

  const fetchGameState = async () => {
    const res = await fetch('http://localhost:3001/api/gameState');
    const data = await res.json();
    setFrames(data.frames);
    setTotalScore(data.totalScore);
    setGameOver(data.gameOver);
    setCurrentFrameIndex(data.currentFrameIndex);
    
    // Handle interactive mode state
    if (data.pinsState) {
      setPinsState(data.pinsState);
      setSelectedPins(data.selectedPins || []);
    }
  };

  const handleSimulate = async () => {
    const res = await fetch('http://localhost:3001/api/simulateRoll', {
      method: 'POST',
    });
    const data = await res.json();
    setFrames(data.frames);
    setTotalScore(data.totalScore);
    setGameOver(data.gameOver);
    setCurrentFrameIndex(data.currentFrameIndex);
    
    // Update pins state for interactive mode
    if (data.pinsState) {
      setPinsState(data.pinsState);
      setSelectedPins(data.selectedPins || []);
    }
  };

  const handleSimulateGame = async () => {
    console.log('Simulating game...');
    const rest = await fetch('http://localhost:3001/api/simulateGame', {
      method: 'POST',
    });
    const data = await rest.json();
    setFrames(data.frames);
    setTotalScore(data.totalScore);
    setGameOver(data.gameOver);
    setCurrentFrameIndex(data.currentFrameIndex);
    
    // Update pins state for interactive mode
    if (data.pinsState) {
      setPinsState(data.pinsState);
      setSelectedPins(data.selectedPins || []);
    }
  };

  const handleReset = async () => {
    const res = await fetch('http://localhost:3001/api/resetGame', {
      method: 'POST',
    });
    const data = await res.json();
    setFrames(data.frames);
    setTotalScore(data.totalScore);
    setGameOver(data.gameOver);
    setCurrentFrameIndex(data.currentFrameIndex);
    
    // Update pins state for interactive mode
    if (data.pinsState) {
      setPinsState(data.pinsState);
      setSelectedPins(data.selectedPins || []);
    }
  };

  const handleUndo = async () => {
    if (isLoading) return;
    setIsLoading(true);
    try {
      const res = await fetch('http://localhost:3001/api/undoRoll', {
        method: 'POST',
      });
      const data = await res.json();
      setFrames(data.frames);
      setTotalScore(data.totalScore);
      setGameOver(data.gameOver);
      setCurrentFrameIndex(data.currentFrameIndex);
      
      // Update pins state for interactive mode
      if (data.pinsState) {
        setPinsState(data.pinsState);
        setSelectedPins(data.selectedPins || []);
      }
    } catch (error) {
      console.error("Error undoing roll:", error);
    } finally {
      setIsLoading(false);
    }
  };

  // Toggle between simulation and interactive mode
  const toggleMode = async () => {
    try {
      const newMode = isInteractive ? 'simulation' : 'interactive';
      const res = await fetch('http://localhost:3001/api/setMode', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ mode: newMode }),
      });
      const data = await res.json();
      
      if (data.success) {
        setIsInteractive(!isInteractive);
        
        // Update game state
        if (data.gameState) {
          setFrames(data.gameState.frames);
          setTotalScore(data.gameState.totalScore);
          setGameOver(data.gameState.gameOver);
          setCurrentFrameIndex(data.gameState.currentFrameIndex);
          
          // Update pins state for interactive mode
          if (data.gameState.pinsState) {
            setPinsState(data.gameState.pinsState);
            setSelectedPins(data.gameState.selectedPins || []);
          }
        }
      }
    } catch (error) {
      console.error("Error toggling game mode:", error);
    }
  };

  // Handle pin toggle in interactive mode
  const handleTogglePin = async (pinIndex) => {
    if (!isInteractive || gameOver) return;
    
    try {
      const res = await fetch('http://localhost:3001/api/togglePin', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ pinIndex }),
      });
      const data = await res.json();
      
      // Update game state including pins
      setFrames(data.frames);
      setTotalScore(data.totalScore);
      setGameOver(data.gameOver);
      setCurrentFrameIndex(data.currentFrameIndex);
      setPinsState(data.pinsState);
      setSelectedPins(data.selectedPins);
    } catch (error) {
      console.error("Error toggling pin:", error);
    }
  };

  // Handle player roll in interactive mode
  const handlePlayerRoll = async () => {
    if (!isInteractive || gameOver || selectedPins.length === 0) return;
    
    try {
      const res = await fetch('http://localhost:3001/api/playerRoll', {
        method: 'POST',
      });
      const data = await res.json();
      
      // Update game state including pins
      setFrames(data.frames);
      setTotalScore(data.totalScore);
      setGameOver(data.gameOver);
      setCurrentFrameIndex(data.currentFrameIndex);
      setPinsState(data.pinsState);
      setSelectedPins(data.selectedPins);
    } catch (error) {
      console.error("Error rolling:", error);
    }
  };

  // Format roll display using bowling notation
  const formatRoll = (frame, rollNum) => {
    if (rollNum === 1) {
      // First roll
      if (frame.roll1 === 10) {
        return 'X'; // Strike
      } else if (frame.roll1 !== undefined) {
        return frame.roll1 === 0 ? '-' : frame.roll1; // Show '-' for gutter balls
      }
      return '';
    } else if (rollNum === 2) {
      // Second roll
      if (frame.roll1 === 10 && frame.roll2 === 10) {
        return 'X'; // Strike in 10th frame
      } else if (frame.roll1 !== undefined && frame.roll2 !== undefined && frame.roll1 + frame.roll2 === 10) {
        return '/'; // Spare
      } else if (frame.roll2 !== undefined) {
        return frame.roll2 === 0 ? '-' : frame.roll2; // Show '-' for gutter balls
      }
      return '';
    } else if (rollNum === 3) {
      // Third roll (only in 10th frame)
      if (frame.roll3 === 10) {
        return 'X'; // Strike in bonus roll
      } else if (frame.roll2 === 10 && frame.roll3 !== undefined && frame.roll3 + frame.roll2 === 10) {
        return '/'; // Spare in bonus roll after strike
      } else if (frame.roll3 !== undefined) {
        return frame.roll3 === 0 ? '-' : frame.roll3; // Show '-' for gutter balls
      }
      return '';
    }
    return '';
  };

  return (
    <div className="simulation-page">
      <div className="simulation-container">
        <table className="bowling-score-table">
          <thead>
            <tr>
              {/* Frame headers: frames 1–9 each have 2 columns, frame 10 has 3 */}
              {[...Array(9)].map((_, i) => (
                <th key={i} colSpan="2">{i + 1}</th>
              ))}
              <th colSpan="3">10</th>
            </tr>
          </thead>
          <tbody>
            {/* First row: display the rolls */}
            <tr>
              {frames.map((frame, i) => {
                // For frames 1–9, show roll1 and roll2
                if (i < 9) {
                  return (
                    <React.Fragment key={i}>
                      <td className="frame-shot">
                        {formatRoll(frame, 1)}
                      </td>
                      <td className="frame-shot">
                        {formatRoll(frame, 2)}
                      </td>
                    </React.Fragment>
                  );
                } 
                // For frame 10, show roll1, roll2, roll3
                else {
                  return (
                    <React.Fragment key={i}>
                      <td className="frame-shot">
                        {formatRoll(frame, 1)}
                      </td>
                      <td className="frame-shot">
                        {formatRoll(frame, 2)}
                      </td>
                      <td className="frame-shot">
                        {formatRoll(frame, 3)}
                      </td>
                    </React.Fragment>
                  );
                }
              })}
            </tr>

            {/* Second row: display each frame's cumulative score */}
            <tr>
              {frames.map((frame, i) => {
                 // Determine if the score for this frame should be displayed
                 const isFrameStarted = frame.roll1 !== undefined;
                 const isPreviousFrame = i < currentFrameIndex;
                 // Show score if:
                 // 1. It's a previous, completed frame OR
                 // 2. It's the current frame AND the first roll has been made OR
                 // 3. The game is over
                 const showScore = isPreviousFrame || (i === currentFrameIndex && isFrameStarted) || gameOver;

                if (i < 9) {
                  return (
                    <td
                      key={`score-${i}`}
                      colSpan="2"
                      className="frame-total"
                    >
                      {showScore && frame.cumulativeScore !== undefined ? frame.cumulativeScore : ''}
                    </td>
                  );
                } else {
                  // 10th frame
                  return (
                    <td
                      key={`score-${i}`}
                      colSpan="3"
                      className="frame-total"
                    >
                      {showScore && frame.cumulativeScore !== undefined ? frame.cumulativeScore : ''}
                    </td>
                  );
                }
              })}
            </tr>
          </tbody>
        </table>
      </div>

      {/* Replace static image with interactive bowling lane component */}
      <div className="bowling-game-container">
        {isInteractive ? (
          <BowlingLane 
            pinsState={pinsState}
            selectedPins={selectedPins}
            onTogglePin={handleTogglePin}
            onRoll={handlePlayerRoll}
            isDisabled={gameOver}
          />
        ) : (
          <div className="interactive-prompt">
            <p>Click "Play" to enter interactive bowling mode!</p>
          </div>
        )}
      </div>

      <div className="simulation-controls">
        <div className="button-row">
          <button 
            type="button" 
            className={`play-button ${isInteractive ? 'active' : ''}`} 
            onClick={toggleMode}
          >
            {isInteractive ? 'Simulation Mode' : 'Play'}
          </button>
          <button 
            type="button" 
            className="play-button" 
            onClick={handleSimulate} 
            disabled={gameOver}
          >
            Simulate
          </button>
          <button 
            type="button" 
            className="play-button" 
            onClick={handleSimulateGame} 
            disabled={gameOver}
          >
            Simulate&nbsp;Game
          </button>
        </div>

        <div className="button-row">
          <button 
            type="button" 
            className="undo-button" 
            onClick={handleUndo} 
            disabled={isLoading}
          >
            Undo
          </button>
          <button 
            type="button" 
            className="reset-button" 
            onClick={handleReset}
          >
            Reset
          </button>
        </div>
      </div>
    </div>
  );
}

export default SimulationPage;
